import BackGround from '../UI/BackGround';
import { Link, Route } from 'react-router-dom';
import Quiz from '../QuizPage/Quiz';
import React, { useState, useContext, useEffect } from "react";
import axios from "axios";


const Categories = () => {

	
const API_ENDPOINT = "http://localhost:5000/";
const url = "http://localhost:5000/categories/getall";

	const [waiting, setWaiting] = useState(true);
	const [loading, setLoading] = useState(false);
	const [error, setError] = useState(false);
	const [categories, setCategories] = useState([]);


	


const fetchQuestions = async (API_ENDPOINT) =>
{
	const response = await axios(url).catch((err) => console.log(err));
    if (response) {
      const data = response.data;
      if (data.length > 0) {
        setCategories(response.data);
        setLoading(false);
        setWaiting(false);
        setError(false);
		

      } else {
        setWaiting(true);
        setError(true);

      }
    } else {
      setWaiting(true);

    }
}


	const category = [
		{
			id: '1',
			title: 'Historia',
		},
		{ id: '2', title: 'Matematyka' },
		{ id: '3', title: 'Muzyka' },
	];

	const buttons = []
	buttons.push(

	)


	useEffect(() =>
	{
		fetchQuestions();
		


	}, [])

	useEffect(()=>{
		categories.forEach((cat) => {
			buttons.push(
		<Link className='text-link' to={`/quiz/${cat.id}`}>
			<button className='btn-ctg'>{cat.category_name}</button>
		</Link>
		);
		})
		
	})
	console.log(buttons)
	return (
		
		<div>
			<BackGround />
			<div class='main-cat'>
				{/* <Link className='text-link' to='/'>
