import './Categories.css';
import BackGround from '../UI/BackGround';
import { Link, Route } from 'react-router-dom';
import Quiz from '../QuizPage/Quiz';
import React, { useState, useContext, useEffect } from "react";
import axios from "axios";

const API_ENDPOINT = "https://localhost:5000/";
const url = "https://localhost:5000/categories/getall";



	const [waiting, setWaiting] = useState(true);
	const [loading, setLoading] = useState(false);
	const [error, setError] = useState(false);
	const [categories, setCategories] = useState([]);

	console.log("xd2")

const fetchQuestions = async (API_ENDPOINT) =>
{
	console.log("xd3")
	const response = await axios(url).catch((err) => console.log(err));
    if (response) {
      const data = response.data.results;
      if (data.length > 0) {
        setCategories(response.data.results);
        setLoading(false);
        setWaiting(false);
        setError(false);
		
console.log(data);
      } else {
        setWaiting(true);
        setError(true);

      }
    } else {
      setWaiting(true);

    }
}


    <AppContext.Provider
      value={{
        waiting,
        loading,
        questions,
        index,
        correct,
        error,
        isModalOpen,
        nextQuestion,
        checkAnswer,
        closeModal,
        quiz,
        handleChange,
        handleSubmit,
      }}
    >
      {children}
    </AppContext.Provider>


const Categories = () => {
	const category = [
		{
			id: '1',
			title: 'Historia',
		},
		{ id: '2', title: 'Matematyka' },
		{ id: '3', title: 'Muzyka' },
	];
	return (
		<div>
			<BackGround />
			<div class='main-cat'>
				<Link className='text-link' to='/'>
					<button className='btn-close'>close</button>
				</Link>
				<label className='lbl-cat' htmlFor='chk' aria-hidden='true'>
					Kategorie
				</label>
				<Link className='text-link' to={`/quiz/${category[0].id}`}>
					<button className='btn-ctg'>{category[0].title}</button>
				</Link>

				<Link className='text-link' to={`/quiz/${category[1].id}`}>
					<button className='btn-ctg'>{category[1].title}</button>
				</Link>
				<Link className='text-link' to={`/quiz/${category[2].id}`}>
					<button className='btn-ctg'>{category[2].title}</button>
				</Link>
			</div>
		</div>
	);
};

export default Categories;
