import './Quiz.css';
import BackGround from '../UI/BackGround';
import { useState } from 'react';

const Quiz = () => {
	const [index, setIndex] = useState(0);
	const [back, setBack] = useState();

	const questions = [
		{
			id: 1,
			question: 'dagfasjkgfnajkdfasdf',

			answer: [
				{
					answerPrefix: 'AAAAA',
					answerText: 'Choice1',
					answerIsTrue: true,
				},
				{
					answerPrefix: 'BBBBB',
					answerText: 'Choice2',
					answerIsTrue: false,
				},
				{
					answerPrefix: 'CCCCC',
					answerText: 'Choice3',
					answerIsTrue: false,
				},
				{
					answerPrefix: 'DDDDD',
					answerText: 'Choice4',
					answerIsTrue: false,
				},
			],
		},
		{
			id: 2,
			question:
				'Reference site about Lorem Ipsum, giving information on its origins, as well as a random Lipsum generator.',

			answer: [
				{
					answerPrefix: 'AAAAA',
					answerText: 'Choice1',
					answerIsTrue: false,
				},
				{
					answerPrefix: 'BBBB',
					answerText: 'Choice2',
					answerIsTrue: true,
				},
				{
					answerPrefix: 'CCCCC',
					answerText: 'Choice3',
					answerIsTrue: false,
				},
				{
					answerPrefix: 'DDDDD',
					answerText: 'Choice4',
					answerIsTrue: false,
				},
			],
		},
	];

	const nextQuestion = () => {
		setIndex((oldIndex) => {
			const index = oldIndex + 1;
			if (index > questions.length - 1) {
				console.log('Koniec gry');
				return 0;
			} else {
				return index;
			}
		});
	};

	return (
		<div>
			<BackGround />
			<div className='container'>
				<div className='justify-center flex-column'>
					<h2 className='question'>{questions[index].question}</h2>

					{questions[index].answer.map((answer) => (
						//onClick -- index++
						<div className='choice-container'>
							<p className='choice-prefix'>{answer.answerPrefix}</p>
							<p className='choice-text'>{answer.answerText}</p>
						</div>
					))}
					<button onClick={nextQuestion} className='btn-next'>
						Next
					</button>
					<button onClick={nextQuestion} className='btn-next'>
						Cofnij
					</button>
				</div>
			</div>
		</div>
	);
};

export default Quiz;
